import React from 'react';
import hand_icon from '../Assets/hand_icon.png';
import arrow_icon from '../Assets/arrow.png';
import hero_image from '../Assets/hero_image.png';

export const Hero = () => {
  return (
    <div className="flex flex-col-reverse md:flex-row items-center justify-between px-6 py-10 bg-white">
      {/* Left Section */}
      <div className="md:w-1/2 text-center md:text-left space-y-6">
        <h2 className="text-3xl md:text-4xl font-bold text-gray-800">NEW ARRIVALS ONLY</h2>

        <div className="space-y-2">
          <div className="flex items-center justify-center md:justify-start gap-2">
            <p className="text-lg font-semibold text-blue-600">New</p>
            <img src={hand_icon} alt="Hand Icon" className="h-6 w-6" />
          </div>
          <p className="text-xl font-medium text-gray-700">Collections</p>
          <p className="text-xl font-medium text-gray-700">for Everyone</p>
        </div>

        <div className="inline-flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-full hover:bg-blue-700 transition">
          <span className="font-semibold">Latest Collection</span>
          <img src={arrow_icon} alt="Arrow Icon" className="h-4 w-4" />
        </div>
      </div>

      {/* Right Section */}
      <div className="md:w-1/2 mb-6 md:mb-0 flex justify-center">
        <img src={hero_image} alt="Hero" className="w-full max-w-md md:max-w-full object-contain" />
      </div>
    </div>
  );
};
